const stats = [
  { number: "50+", label: "Countries" },
  { number: "200+", label: "Tours" },
  { number: "10k+", label: "Happy Customers" },
  { number: "15+", label: "Years Experience" },
];

const Home = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [openBooking, setOpenBooking] = useState(false);
  const [selectedDestination, setSelectedDestination] = useState(null);
  const [bookingData, setBookingData] = useState({
    name: '',
    email: '',
    phone: '',
    date: '',
    guests: 1
  });
  const [errors, setErrors] = useState({});
  const [openSnackbar, setOpenSnackbar] = useState(false);

  useEffect(() => {
    if (location.state?.scrollTo) {
      const section = document.getElementById(location.state.scrollTo);
      if (section) {
        setTimeout(() => {
          section.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }, 100);
      }
    }
  }, [location]);

  const handleExploreClick = () => {
    try {
      navigate('/destinations');
    } catch (error) {
      console.error('Navigation failed:', error);
      window.location.href = '/destinations';
    }
  };

  const handleBookNow = (destination) => {
    setSelectedDestination(destination);
    setOpenBooking(true);
  };

  const handleCloseBooking = () => {
    setOpenBooking(false);
    setSelectedDestination(null);
    setBookingData({
      name: '',
      email: '',
      phone: '',
      date: '',
      guests: 1
    });
    setErrors({});
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!bookingData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!bookingData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!emailRegex.test(bookingData.email)) {
      newErrors.email = 'Invalid email format';
    }

    const phoneRegex = /^\+?[\d\s-]{10,}$/;
    if (!bookingData.phone.trim()) {
      newErrors.phone = 'Phone number is required';
    } else if (!phoneRegex.test(bookingData.phone)) {
      newErrors.phone = 'Invalid phone number';
    }

    const selectedDate = new Date(bookingData.date);
    const today = new Date();
    if (!bookingData.date) {
      newErrors.date = 'Date is required';
    } else if (selectedDate < today) {
      newErrors.date = 'Date cannot be in the past';
    }

    if (bookingData.guests < 1) {
      newErrors.guests = 'At least 1 guest is required';
    } else if (bookingData.guests > 10) {
      newErrors.guests = 'Maximum 10 guests allowed';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleBookingSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Booking submitted:', {
        destination: selectedDestination,
        ...bookingData
      });
      handleCloseBooking();
      setOpenSnackbar(true);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBookingData(prev => ({
      ...prev,
      [name]: value
    }));
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  return (
    <Box sx={{ minHeight: '100vh' }}>
      {/* Hero Section */}
      <Box
        sx={{
          height: '100vh',
          position: 'relative',
          overflow: 'hidden',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        {/* Background Image */}
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundImage: `url(${backgroundImages[0]})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: 'linear-gradient(135deg, rgba(0,0,0,0.4) 0%, rgba(0,0,0,0.7) 100%)',
              zIndex: 1,
            },
          }}
        />

        {/* Content */}
        <Box
          sx={{
            position: 'relative',
            zIndex: 2,
            textAlign: 'center',
            padding: 4,
            color: 'white',
          }}
        >
          <Typography
            variant="h1"
            sx={{
              fontSize: { xs: '2.5rem', md: '4rem' },
              marginBottom: 2,
              color: '#FFD700',
              textShadow: '2px 2px 4px rgba(0,0,0,0.5)',
            }}
          >
            Explore the World
          </Typography>
          <Typography
            variant="h5"
            sx={{
              marginBottom: 4,
              fontStyle: 'italic',
              color: '#FFFFFF',
              textShadow: '1px 1px 2px rgba(0,0,0,0.5)',
            }}
          >
            Discover amazing places and create unforgettable memories
          </Typography>
          <Button
            variant="contained"
            size="large"
            onClick={handleExploreClick}
            sx={{
              fontSize: '1.3rem',
              padding: '15px 40px',
              borderRadius: '35px',
              background: 'linear-gradient(45deg, #FF6B6B, #FF8E53)',
              boxShadow: '0 4px 15px rgba(255,107,107,0.3)',
              border: '2px solid rgba(255,255,255,0.1)',
              cursor: 'pointer',
            }}
          >
            Explore Destinations
          </Button>
        </Box>
      </Box>

      {/* Image Gallery */}
      <Box sx={{ py: 6, backgroundColor: 'background.paper' }}>
        <Container>
          <Typography
            variant="h2"
            align="center"
            sx={{ 
              mb: 4, 
              fontWeight: 'bold', 
              color: 'primary.main',
              textShadow: '1px 1px 2px rgba(0,0,0,0.1)'
            }}
          >
            Popular Destinations
          </Typography>
          
          <Grid container spacing={3}>
            {travelImages.map((image, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <Card sx={{ height: '300px', position: 'relative', overflow: 'hidden', boxShadow: 3 }}>
                  <CardMedia
                    component="img"
                    height="300"
                    image={image.url}
                    alt={image.title}
                  />
                  <Box
                    sx={{
                      position: 'absolute',
                      bottom: 0,
                      left: 0,
                      right: 0,
                      bgcolor: 'rgba(0, 0, 0, 0.5)',
                      color: 'white',
                      padding: 2,
                    }}
                  >
                    <Typography variant="h6">{image.title}</Typography>
                  </Box>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* Features Section */}
      <Container>
        <Box sx={{ py: 8 }}>
          <Typography variant="h3" sx={{ textAlign: 'center', mb: 4 }}>
            Why Choose Us
          </Typography>
          <Grid container spacing={4}>
            {features.map((feature, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <Card sx={{ height: '100%' }}>
                  <CardContent>
                    <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
                      {feature.icon}
                    </Box>
                    <Typography variant="h6" sx={{ mb: 2 }}>
                      {feature.title}
                    </Typography>
                    <Typography>
                      {feature.description}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>

      {/* Stats Section */}
      <Box sx={{ py: 8, backgroundColor: '#f5f5f5' }}>
        <Container>
          <Grid container spacing={4}>
            {stats.map((stat, index) => (
              <Grid item xs={6} md={3} key={index}>
                <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                  <Typography variant="h3" sx={{ mb: 1 }}>
                    {stat.number}
                  </Typography>
                  <Typography variant="h6" color="textSecondary">
                    {stat.label}
                  </Typography>
                </Box>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* Featured Destinations Section */}
      <Box sx={{ backgroundColor: '#f5f5f5', py: 8 }}>
        <Container>
          <Typography
            variant="h2"
            component="h2"
            sx={{ 
              textAlign: 'center',
              fontWeight: 800,
              background: 'linear-gradient(45deg, #1976d2 30%, #2196f3 90%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              backgroundClip: 'text',
              mb: 2
            }}
          >
            Featured Destinations
          </Typography>
          <Typography
            variant="h5"
            color="text.secondary"
            align="center"
            sx={{ mb: 6 }}
          >
            Explore our handpicked destinations for your next adventure
          </Typography>
          <Grid container spacing={4}>
            {featuredDestinations.map((destination, index) => (
              <Grid item xs={12} md={4} key={index}>
                <Card className="destination-card" elevation={0}>
                  <Box sx={{ position: 'relative', overflow: 'hidden' }}>
                    <CardMedia
                      className="destination-image"
                      component="img"
                      image={destination.image}
                      alt={destination.title}
                    />
                    <Box 
                      className="destination-overlay"
                      sx={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        background: 'linear-gradient(to bottom, rgba(0,0,0,0) 0%, rgba(0,0,0,0.4) 100%)',
                        opacity: 0,
                        transition: 'opacity 0.3s ease',
                        display: 'flex',
                        alignItems: 'flex-end',
                        padding: 2,
                        '&:hover': {
                          opacity: 1
                        }
                      }}
                    >
                      <Typography variant="body2" sx={{ color: 'white' }}>
                        <i className="fas fa-map-marker-alt" sx={{ mr: 0.5, verticalAlign: 'middle' }} />
                        {destination.location}
                      </Typography>
                    </Box>
                  </Box>
                  <CardContent className="destination-content">
                    <Typography variant="h5" component="h3" className="destination-title">
                      {destination.title}
                    </Typography>
                    <Typography variant="body2" className="destination-description">
                      {destination.description}
                    </Typography>
                    <div className="destination-info">
                      <Typography variant="body2" className="destination-duration">
                        <i className="fas fa-clock" sx={{ fontSize: 16 }} />
                        {destination.duration}
                      </Typography>
                      <Typography variant="body2" className="destination-price">
                        <i className="fas fa-dollar-sign" sx={{ fontSize: 16 }} />
                        ${destination.price}
                      </Typography>
                    </div>
                    <div className="activities-container">
                      {destination.activities.map((activity, idx) => (
                        <span key={idx} className="activity-chip">
                          {activity}
                        </span>
                      ))}
                    </div>
                    <Button
                      variant="contained"
                      fullWidth
                      className="book-now-button"
                      onClick={() => handleBookNow(destination)}
                    >
                      Book Now
                    </Button>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* Testimonials Section */}
      <Box sx={{ py: 8 }}>
        <Container>
          <Typography variant="h3" sx={{ textAlign: 'center', mb: 4 }}>
            What Our Travelers Say
          </Typography>
          <Grid container spacing={4}>
            {testimonials.map((testimonial) => (
              <Grid item xs={12} md={4} key={testimonial.id}>
                <Card sx={{ height: '100%' }}>
                  <CardContent>
                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                      <img src={testimonial.avatar} alt={testimonial.name} style={{ width: 40, height: 40, borderRadius: '50%', marginRight: 16 }} />
                      <Box>
                        <Typography variant="h6">
                          {testimonial.name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          {testimonial.role}
                        </Typography>
                      </Box>
                    </Box>
                    <Typography variant="body2" sx={{ mb: 2 }}>
                      {testimonial.text}
                    </Typography>
                    <Box sx={{ display: 'flex', justifyContent: 'center' }}>
                      {[1, 2, 3, 4, 5].map((rating, index) => (
                        <i key={index} className={`fas fa-star${rating <= testimonial.rating ? '' : '-o'}`} sx={{ fontSize: 16, color: 'warning.main', marginRight: 0.5 }} />
                      ))}
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* Newsletter Section */}
      <Box sx={{ py: 8, backgroundColor: '#f5f5f5' }}>
        <Container>
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <Typography variant="h3" sx={{ mb: 2 }}>
              Get Travel Updates
            </Typography>
            <Typography variant="h6" color="textSecondary" sx={{ mb: 4 }}>
              Subscribe to our newsletter for exclusive deals and travel tips
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <input type="email" placeholder="Enter your email" style={{ padding: '10px', fontSize: 16, border: '1px solid #ccc', borderRadius: 4, width: '100%' }} />
              <Button variant="contained" size="small" sx={{ ml: 2 }}>
                Subscribe
              </Button>
            </Box>
          </Box>
        </Container>
      </Box>

      {/* Social Media Section */}
      <Box sx={{ py: 8 }}>
        <Container>
          <Box sx={{ display: 'flex', justifyContent: 'center' }}>
            <i className="fab fa-facebook" style={{ fontSize: 24, color: '#4267B2', marginRight: 16 }} />
            <i className="fab fa-twitter" style={{ fontSize: 24, color: '#1DA1F2', marginRight: 16 }} />
            <i className="fab fa-instagram" style={{ fontSize: 24, color: '#E1306C' }} />
          </Box>
        </Container>
      </Box>

      {/* Booking Modal */}
      <Dialog
        open={openBooking}
        onClose={handleCloseBooking}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            Book Your Trip to {selectedDestination?.title}
            <i className="fas fa-times" style={{ fontSize: 20, cursor: 'pointer' }} onClick={handleCloseBooking} />
          </Box>
        </DialogTitle>
        <form onSubmit={handleBookingSubmit}>
          <DialogContent>
            {selectedDestination && (
              <Box mb={3}>
                <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                  {selectedDestination.duration} | ${selectedDestination.price} per person
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {selectedDestination.description}
                </Typography>
                <Box mt={2} p={2} bgcolor="#f8f9fa" borderRadius={1}>
                  <Typography variant="body2" color="text.secondary">
                    Total Price: ${selectedDestination.price * bookingData.guests}
                  </Typography>
                </Box>
              </Box>
            )}
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <input
                  required
                  fullWidth
                  type="text"
                  label="Full Name"
                  name="name"
                  value={bookingData.name}
                  onChange={handleInputChange}
                  error={!!errors.name}
                  helperText={errors.name}
                  style={{ padding: '10px', fontSize: 16, border: '1px solid #ccc', borderRadius: 4 }}
                />
              </Grid>
              <Grid item xs={12}>
                <input
                  required
                  fullWidth
                  type="email"
                  label="Email"
                  name="email"
                  value={bookingData.email}
                  onChange={handleInputChange}
                  error={!!errors.email}
                  helperText={errors.email}
                  style={{ padding: '10px', fontSize: 16, border: '1px solid #ccc', borderRadius: 4 }}
                />
              </Grid>
              <Grid item xs={12}>
                <input
                  required
                  fullWidth
                  type="text"
                  label="Phone Number"
                  name="phone"
                  value={bookingData.phone}
                  onChange={handleInputChange}
                  error={!!errors.phone}
                  helperText={errors.phone}
                  placeholder="+1 234 567 8900"
                  style={{ padding: '10px', fontSize: 16, border: '1px solid #ccc', borderRadius: 4 }}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <input
                  required
                  fullWidth
                  type="date"
                  label="Travel Date"
                  name="date"
                  value={bookingData.date}
                  onChange={handleInputChange}
                  error={!!errors.date}
                  helperText={errors.date}
                  style={{ padding: '10px', fontSize: 16, border: '1px solid #ccc', borderRadius: 4 }}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <input
                  required
                  fullWidth
                  type="number"
                  label="Number of Guests"
                  name="guests"
                  value={bookingData.guests}
                  onChange={handleInputChange}
                  error={!!errors.guests}
                  helperText={errors.guests}
                  style={{ padding: '10px', fontSize: 16, border: '1px solid #ccc', borderRadius: 4 }}
                />
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseBooking} color="inherit">
              Cancel
            </Button>
            <Button
              type="submit"
              variant="contained"
              className="book-now-button"
            >
              Confirm Booking
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      {/* Success Snackbar */}
      <Snackbar
        open={openSnackbar}
        autoHideDuration={6000}
        onClose={() => setOpenSnackbar(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert
          onClose={() => setOpenSnackbar(false)}
          severity="success"
          variant="filled"
        >
          Booking submitted successfully! We'll contact you soon.
        </Alert>
      </Snackbar>

      {/* Footer */}
      <Footer />
    </Box>
  );
};

export default Home;
